mapper.select:
select
FROM HZ_MBOM_RECORD T LEFT JOIN HZ_PBOM_RECORD T2 ON T.P_BOM_PUID=T2.P_E_BOM_PUID LEFT JOIN HZ_BOM_LINE_RECORD T3 ON T3.PUID=T.P_BOM_PUID LEFT JOIN HZ_FACTORY T6 ON T6.PUID=T.P_FACTORY_ID /*关联相关性*/ LEFT JOIN Hz_Cfg0_Of_Bomline_Record T7 on t7.p_bomlinepuid=t.p_bom_line_id LEFT JOIN Hz_Cfg0_Record T8 on T8.PUID=T7.P_TO_CFG0_ID_OF_BL
WHERE T.P_BOM_DIGIFAX_ID=( SELECT T4.PUID FROM HZ_BOM_MAIN_RECORD T4 WHERE T4.P_CFG0_OF_WHICH_PROJECT_PUID=( SELECT T5.PUID FROM HZ_PROJECT_LIBS T5 WHERE T5.PUID =#{projectUid,jdbcType=VARCHAR} AND ${flag} IN
#{item,jdbcType=VARCHAR}
('')
) )

select
t.PUID,t.P_PARENT_UID,t.P_BOM_PUID
from Hz_Mbom_Record t where t.p_bom_digifax_id=(
select puid from Hz_Bom_Main_Record t2 where t2.p_cfg0_of_which_project_puid=#{projectUid,jdbcType=VARCHAR}
)

    and t.p_bom_line_is_2y=0


    and t.p_bom_line_is_2y=1
