mapper.select:
select

from HZ_FULL_CFG_WITHCFG_RECORD
where FL_CFG_VERSION = #{flCfgVersion,jdbcType=DECIMAL}
and CFG_BOMLINE_UID=#{cfgBomlineUid,jdbcType=VARCHAR}

mapper.insert:
insert into HZ_FULL_CFG_WITHCFG_RECORD (ID, CFG_CFG0_UID, FL_CFG_CREATOR, FL_CFG_UPDATOR, FL_CFG_VERSION ) select SEQ_HZ_FL_CFG_CFG0_ID.NEXTVAL ,A.* from(
select #{hzFullCfgWithCfg.cfgCfg0Uid,jdbcType=VARCHAR}, #{hzFullCfgWithCfg.flCfgCreator,jdbcType=VARCHAR}, #{hzFullCfgWithCfg.flCfgUpdator,jdbcType=VARCHAR}, #{hzFullCfgWithCfg.flCfgVersion,jdbcType=DECIMAL} from dual
)A

insert into HZ_FULL_CFG_WITHCFG_RECORD (ID, CFG_BOMLINE_UID, FL_CFG_BOMLINE_NAME, FL_CFG_CREATOR, FL_CFG_UPDATOR, FL_CFG_VERSION ) select SEQ_HZ_FL_CFG_CFG0_ID.NEXTVAL ,A.* from(
select #{hzFullCfgWithCfg.cfgBomlineUid,jdbcType=VARCHAR}, #{hzFullCfgWithCfg.flCfgBomlineName,jdbcType=VARCHAR}, #{hzFullCfgWithCfg.flCfgCreator,jdbcType=VARCHAR}, #{hzFullCfgWithCfg.flCfgUpdator,jdbcType=VARCHAR}, #{hzFullCfgWithCfg.flCfgVersion,jdbcType=DECIMAL} from dual
)A
