mapper.select:
select t.p_brand_code, t.p_brand_name, t2.p_platform_code, t2.p_platform_name, t3.p_vehicle_code, t3.p_vehicle_name, t4.p_in_color_code, t4.p_in_color_name, t4.p_color_code, t4.p_color_name, t4.p_battery_model, t4.p_motor_model, t6.object_name, t6.puid, t6.is_sent from Hz_Brand_Record t left join Hz_Platform_Record t2 on t.puid=t2.p_pertain_to_brand_puid left join Hz_Vehicle_Record t3 on t2.puid=t3.p_vehicle_pertain_to_platform left join Hz_Project_Libs t4 on t3.puid=t4.p_project_pertain_to_vehicle left join Hz_Cfg0_Main_Record t5 on t5.p_cfg0_of_which_project_puid=t4.puid left join Hz_Cfg0_Model_Record t6 on t6.p_cfg0_model_of_main_record=t5.puid
where t4.puid=#{projectUid,jdbcType="VARCHAR"} ;
where t6.puid=#{uid,jdbcType="VARCHAR"} ;
