mapper.select:
select * FROM (SELECT u.*,rownum as rn from (SELECT t1.*, T2.USER_NAME as CREATE_NAME, t3.USER_NAME as ORIGINATOR, t4. NAME as DEPT_NAME FROM HZ_CHANGE_ORDER_RECORD t1 LEFT JOIN USERRECORD t2 ON t1.CREATE_ID = t2. ID LEFT JOIN USERRECORD t3 ON t1.ORIGINATOR_ID = t3. ID JOIN ORG_GROUP t4 ON t1.dept_ID = t4. GROUP_ID
1=1
and t1.ID = #{id,jdbcType=INTEGER}
and t1.CHANGE_NO = #{changeNo,jdbcType=VARCHAR}
and t1.ORIGINATOR = #{originator,jdbcType=VARCHAR}
and (t1.ORIGIN_TIME between to_date(#{firstOriginTime,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') and to_date(#{lastOriginTime,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss'))
)u)

select * FROM
(SELECT u.*,rownum as rn from
(SELECT t1.*
FROM
    HZ_CHANGE_ORDER_RECORD t1

  1=1

    and t1.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


    and t1.CHANGE_NO=#{changeNo,jdbcType=VARCHAR}


    and t1.STATE=#{state,jdbcType=INTEGER}

  order by t1.CREATE_TIME desc
  )u

WHERE ROWNUM <=#{limit,jdbcType=INTEGER}) where rn >#{offset,jdbcType=INTEGER}

SELECT count(*)
 FROM
 HZ_CHANGE_ORDER_RECORD t1

   1=1

     and t1.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


     and t1.CHANGE_NO=#{changeNo,jdbcType=VARCHAR}


     and t1.STATE=#{state,jdbcType=INTEGER}
