update.foreach:
update HZ_EPL_RECORD
UPDATE_TIME = sysdate, STATUS = 4
where ID = #{list[${index}]}

mapper.select:
select * from (
select u.*,rownum as rn from(
select

from HZ_EPL_RECORD t1

    1=1

        and t1.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


        and t1.PART_ID like CONCAT(CONCAT('%',#{partId,jdbcType=VARCHAR}),'%')


        and t1.PART_NAME like CONCAT(CONCAT('%',#{partName,jdbcType=VARCHAR}),'%')


        and t1.PART_OF_WHICH_DEPT = #{partOfWhichDept,jdbcType=VARCHAR}


        and t1.PART_RESOURCE = #{partResource,jdbcType=VARCHAR}


        and t1.STATUS = #{status,jdbcType=VARCHAR}

    and t1.STATUS  <> 0 order by t1.PART_ID

)u
where ROWNUM <=#{limit,jdbcType=INTEGER} )
where rn >#{offset,jdbcType=INTEGER}

select
count (*)
from HZ_EPL_RECORD t1

    1=1

        and t1.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


        and t1.PART_ID like CONCAT(CONCAT('%',#{partId,jdbcType=VARCHAR}),'%')


        and t1.PART_NAME like CONCAT(CONCAT('%',#{partName,jdbcType=VARCHAR}),'%')


        and t1.PART_OF_WHICH_DEPT = #{partOfWhichDept,jdbcType=VARCHAR}


        and t1.PART_RESOURCE = #{partResource,jdbcType=VARCHAR}


        and t1.STATUS = #{status,jdbcType=VARCHAR}

    and t1.STATUS  <> 0

select

from HZ_EPL_RECORD t1

    1=1

        and t1.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


        and t1.PART_ID =#{partId,jdbcType=VARCHAR}


        and t1.ID =#{id,jdbcType=DOUBLE}

    and t1.STATUS  <> 0

mapper.insert:
insert into HZ_EPL_RECORD (ID, PROJECT_ID, PART_ID, PART_OF_WHICH_DEPT, PART_NAME, PART_CLASS, PART_EN_NAME, PART_RESOURCE, STATUS, CREATE_TIME, UPDATE_TIME, FASTENER, IS_3CPART, IN_OUT_SIDE_FLAG, CREATE_ID, UPDATE_ID, UNIT, PICTURE_NO, PICTURE_SHEET, MATERIAL_HIGH, MATERIAL1, MATERIAL2, MATERIAL3, DENSITY, MATERIAL_STANDARD, SURFACE_TREAT, TEXTURE_COLOR_NUM, MANU_PROCESS, SYMMETRY, IMPORTANCE, REGULATION_FLAG, BWG_BOX_PART, DEVELOP_TYPE, DATA_VERSION, TARGET_WEIGHT, FEATURE_WEIGHT, ACTUAL_WEIGHT, FASTENER_STANDARD, FASTENER_LEVEL, TORQUE, DUTY_ENGINEER, SUPPLY, SUPPLY_CODE, REMARK, REGULATION_CODE, BUY_ENGINEER) select SEQ_HZ_EPL_RECORD.nextval, A.* from(
select #{item.projectId,jdbcType=VARCHAR}, #{item.partId,jdbcType=VARCHAR}, #{item.partOfWhichDept,jdbcType=VARCHAR}, #{item.partName,jdbcType=VARCHAR}, #{item.partClass,jdbcType=VARCHAR}, #{item.partEnName,jdbcType=VARCHAR}, #{item.partResource,jdbcType=VARCHAR}, 1, #{item.createTime,jdbcType=DATE}, #{item.updateTime,jdbcType=DATE}, #{item.fastener,jdbcType=VARCHAR}, #{item.is3cpart,jdbcType=INTEGER}, #{item.inOutSideFlag,jdbcType=INTEGER}, #{item.createId,jdbcType=DOUBLE}, #{item.updateId,jdbcType=DOUBLE}, #{item.unit,jdbcType=VARCHAR}, #{item.pictureNo,jdbcType=VARCHAR}, #{item.pictureSheet,jdbcType=VARCHAR}, #{item.materialHigh,jdbcType=VARCHAR}, #{item.material1,jdbcType=VARCHAR}, #{item.material2,jdbcType=VARCHAR}, #{item.material3,jdbcType=VARCHAR}, #{item.density,jdbcType=VARCHAR}, #{item.materialStandard,jdbcType=VARCHAR}, #{item.surfaceTreat,jdbcType=VARCHAR}, #{item.textureColorNum,jdbcType=VARCHAR}, #{item.manuProcess,jdbcType=VARCHAR}, #{item.symmetry,jdbcType=VARCHAR}, #{item.importance,jdbcType=VARCHAR}, #{item.regulationFlag,jdbcType=INTEGER}, #{item.bwgBoxPart,jdbcType=VARCHAR}, #{item.developType,jdbcType=VARCHAR}, #{item.dataVersion,jdbcType=VARCHAR}, #{item.targetWeight,jdbcType=VARCHAR}, #{item.featureWeight,jdbcType=VARCHAR}, #{item.actualWeight,jdbcType=VARCHAR}, #{item.fastenerStandard,jdbcType=VARCHAR}, #{item.fastenerLevel,jdbcType=VARCHAR}, #{item.torque,jdbcType=VARCHAR}, #{item.dutyEngineer,jdbcType=VARCHAR}, #{item.supply,jdbcType=VARCHAR}, #{item.supplyCode,jdbcType=VARCHAR}, #{item.remark,jdbcType=VARCHAR}, #{item.regulationCode,jdbcType=VARCHAR}, #{item.buyEngineer,jdbcType=VARCHAR} from dual
)A
