mapper.select:
select
, t6.p_feature_single_vehicle_code as svl_Material_Code, t6.p_feature_cn_desc as svl_Material_Basic_Info, , t9.p_color_name, t9.p_color_code from Hz_single_vehicles t left join Hz_Cfg0_Model_Feature t6 on t.svl_cfg_material_uid = t6.puid left join Hz_Cfg0_Model_Record t7 on t6.p_pertain_to_model = t6.puid left join Hz_Cfg0_Main_Record t8 on t7.p_cfg0_model_of_main_record = t8.puid left join Hz_Cfg0_Model_Color_Record t20 on t6.p_pertain_to_color_model = t20.puid left join Hz_Cfg0_Colorset t9 on t20.p_color_uid = t9.puid left join Hz_Project_Libs t2 on t.svl_project_uid = t2.puid left join Hz_Vehicle_Record t3 on t3.puid = t2.p_project_pertain_to_vehicle left join Hz_Platform_Record t4 on t4.puid = t3.p_vehicle_pertain_to_platform left join Hz_Brand_Record t5 on t5.puid = t4.puid
t.svl_project_uid=#{svlProjectUid,jdbcType=VARCHAR}
and t.SVL_DMB_ID=#{svlDmbId,jdbcType=VARCHAR}
