mapper.select:
select * FROM (SELECT u.*,rownum as rn from (SELECT * from HZ_SINGLE_VEHICLES_BOM where p_bom_digifax_id=( select t3.puid from Hz_Bom_Main_Record t3 where t3.p_cfg0_of_which_project_puid=( select t2.puid from Hz_project_libs t2
t2.puid=#{projectId,jdbcType=VARCHAR}))
and P_SINGLE_VEHICLES_ID = #{singleVehiclesId,jdbcType=VARCHAR}
and LENGTH(TRANSLATE(P_LINE_INDEX,',0123456789','.'))=#{lineIndex,jdbcType=VARCHAR}
and P_BOM_LINE_IS_HAS = 0
and P_BOM_LINE_IS_HAS = 1
and P_BOM_LINE_ID like CONCAT(CONCAT('%',#{lineId,jdbcType=VARCHAR}),'%')
and P_BOM_OF_WHICH_DEPT like CONCAT(CONCAT('%',#{pBomOfWhichDept,jdbcType=VARCHAR}),'%')
and P_BOM_LINE_PART_CLASS = #{pBomLinePartClass,jdbcType=VARCHAR}
and P_BOM_LINE_PART_RESOURCE = #{pBomLinePartResource,jdbcType=VARCHAR}
order by CAST(P_SORT_NUM as FLOAT))u
WHERE ROWNUM <=#{limit,jdbcType=INTEGER}) where rn >#{offset,jdbcType=INTEGER}

select count(*) from  HZ_SINGLE_VEHICLES_BOM t
where t.p_bom_digifax_id=(
select t3.puid from Hz_Bom_Main_Record t3 where t3.p_cfg0_of_which_project_puid=(
select t2.puid from Hz_project_libs t2

    t2.puid=#{projectId,jdbcType=VARCHAR}))

        and P_SINGLE_VEHICLES_ID = #{singleVehiclesId,jdbcType=VARCHAR}


        and LENGTH(TRANSLATE(t.P_LINE_INDEX,',0123456789','.'))=#{lineIndex,jdbcType=VARCHAR}


        and (t.P_BOM_LINE_IS_HAS = 0)


        and t.P_BOM_LINE_IS_HAS = 1


        and t.P_BOM_LINE_ID like CONCAT(CONCAT('%',#{lineId,jdbcType=VARCHAR}),'%')


        and t.P_BOM_OF_WHICH_DEPT like CONCAT(CONCAT('%',#{pBomOfWhichDept,jdbcType=VARCHAR}),'%')


        and t.P_BOM_LINE_PART_CLASS = #{pBomLinePartClass,jdbcType=VARCHAR}


        and t.P_BOM_LINE_PART_RESOURCE = #{pBomLinePartResource,jdbcType=VARCHAR}

mapper.insert:
insert into HZ_SINGLE_VEHICLES_BOM (ID, P_SPARE_PART, P_SPARE_PART_NUM, P_PROCESS_ROUTE, P_LABOR_HOUR, P_RHYTHM, P_SOLDER_JOINT, P_MACHINE_MATERITAL, P_STANDARD_PART, P_TOOLS, P_WASTER_PRODUCT, P_CHANGE, P_CHANGE_NUM, P_MBOM_PUID, P_CREATE_TIME, P_UPDATE_TIME, P_CREATE_NAME, P_UPDATE_NAME, P_PARENT_UID, P_BOM_DIGIFAX_ID, P_LINE_INDEX, P_BOM_LINE_IS_HAS, P_BOM_LINE_ID, P_BOM_LINE_IS_2Y, P_BOM_LINE_IS_PART, P_BOM_LINE_IS_DEPT, P_BOM_OF_WHICH_DEPT, P_BOM_LINE_PART_NAME, P_BOM_LINE_PART_CLASS, P_BOM_LINE_PART_EN_NAME, P_BOM_LINE_PART_RESOURCE, P_STOCK_LOCATION, P_FACTORY_ID, P_SORT_NUM, P_SINGLE_VEHICLES_ID, P_EBOM_PUID,P_BOM_TYPE ) select SEQ_HZ_SINGLE_VEHICLES_BOM.nextval,t.* from (
select #{item.sparePart,jdbcType=VARCHAR}, #{item.sparePartNum,jdbcType=VARCHAR}, #{item.processRoute,jdbcType=VARCHAR}, #{item.laborHour,jdbcType=VARCHAR}, #{item.rhythm,jdbcType=VARCHAR}, #{item.solderJoint,jdbcType=VARCHAR}, #{item.machineMaterial,jdbcType=VARCHAR}, #{item.standardPart,jdbcType=VARCHAR}, #{item.tools,jdbcType=VARCHAR}, #{item.wasterProduct,jdbcType=VARCHAR}, #{item.change,jdbcType=VARCHAR}, #{item.changeNum,jdbcType=VARCHAR}, #{item.mBomPuid,jdbcType=VARCHAR}, #{item.createTime,jdbcType=TIMESTAMP}, #{item.updateTime,jdbcType=TIMESTAMP}, #{item.createName,jdbcType=VARCHAR}, #{item.updateName,jdbcType=VARCHAR}, #{item.parentUid,jdbcType=VARCHAR}, #{item.bomDigifaxId,jdbcType=VARCHAR}, #{item.lineIndex,jdbcType=VARCHAR}, #{item.isHas,jdbcType=INTEGER}, #{item.lineId,jdbcType=VARCHAR}, #{item.is2Y,jdbcType=INTEGER}, #{item.isPart,jdbcType=INTEGER}, #{item.isDept,jdbcType=INTEGER}, #{item.pBomOfWhichDept,jdbcType=VARCHAR}, #{item.pBomLinePartName,jdbcType=VARCHAR}, #{item.pBomLinePartClass,jdbcType=VARCHAR}, #{item.pBomLinePartEnName,jdbcType=VARCHAR}, #{item.pBomLinePartResource,jdbcType=VARCHAR}, #{item.pStockLocation,jdbcType=VARCHAR}, #{item.pFactoryId,jdbcType=VARCHAR}, #{item.sortNum,jdbcType=VARCHAR}, #{item.singleVehiclesId,jdbcType=DOUBLE}, #{item.eBomPuid,jdbcType=VARCHAR},#{item.bomType,jdbcType=INTEGER} from DUAL
)t

select.if:
select * from(
select u.*,rownum as rn from (

    select * from
    (
    select * from HZ_SINGLE_VEHICLES_BOM t
    where t.p_bom_digifax_id=(
    select t3.puid from Hz_Bom_Main_Record t3 where t3.p_cfg0_of_which_project_puid=(
    select t2.puid from Hz_project_libs t2

        t2.puid = #{projectId,jdbcType=VARCHAR}))

    start with t.P_EBOM_PUID = #{eBomPuids[${index}]}

        and P_SINGLE_VEHICLES_ID = #{singleVehiclesId,jdbcType=VARCHAR}

    connect by nocycle prior t.P_EBOM_PUID = t.p_parent_uid

        and P_SINGLE_VEHICLES_ID = #{singleVehiclesId,jdbcType=VARCHAR}

    order by CAST(t.P_SORT_NUM as FLOAT)
    )

) u
where  ROWNUM <=#{limit,jdbcType=INTEGER})
where  rn >#{offset,jdbcType=INTEGER}

select sum(c) from
(

    select count(*) c
    from HZ_SINGLE_VEHICLES_BOM t
    where t.p_bom_digifax_id=(
    select t3.puid from Hz_Bom_Main_Record t3 where t3.p_cfg0_of_which_project_puid=(
    select t2.puid from Hz_project_libs t2

        t2.puid = #{projectId,jdbcType=VARCHAR}))

    start with t.P_EBOM_PUID = #{eBomPuids[${index}]}

        and P_SINGLE_VEHICLES_ID = #{singleVehiclesId,jdbcType=VARCHAR}

    connect by nocycle prior t.P_EBOM_PUID = t.p_parent_uid

        and P_SINGLE_VEHICLES_ID = #{singleVehiclesId,jdbcType=VARCHAR}


)
