mapper.select:
select

from
HZ_DM_BASIC_CHANGE
where
ID = (
  select Max(ID)from HZ_DM_BASIC_CHANGE where DMB_SRC_ID  = #{dmbSrcId,jdbcType=DECIMAL}
  and DMB_CHANGE_STATUS =1
  and ID < #{id,jdbcType=DECIMAL}
)
order by ID

